export const getBrandLogo = (brandName: string): string => {
  if (!brandName) {
    return '/logos/placeholder.svg';
  }

  // 1) Exact match
  if (BRAND_LOGO_MAP[brandName]) return BRAND_LOGO_MAP[brandName];

  const normalizedName = brandName.trim().toLowerCase();
  // 2) Case-insensitive match
  if (NORMALIZED_BRAND_LOGO_MAP[normalizedName]) return NORMALIZED_BRAND_LOGO_MAP[normalizedName];

  // 3) Slug-based match
  const s = slug(brandName);
  if (SLUG_MAP[s]) return SLUG_MAP[s];

  // 4) Alias-based match (map to canonical slug then lookup)
  const canonicalSlug = ALIAS_SLUG_MAP[s];
  if (canonicalSlug && SLUG_MAP[canonicalSlug]) return SLUG_MAP[canonicalSlug];

  return '/logos/placeholder.svg';
};
